import React from 'react';
import './BackCard.css';

const BackCard = () => {
    const [isFlipped, setIsFlipped] = useState(false);

    const handleClick = () => {
        setIsFlipped(!isFlipped);
    };

    return (
        <div className={`w-72 h-96 bg-white shadow-lg rounded-lg p-4 cursor-pointer backCard ${isFlipped ? 'flipped' : ''}`} onClick={handleClick}>
            <div className='front'></div>
            <div className='back'></div>
        </div>
    );
}


export default BackCard;
