import React, { useState } from 'react';
import Card from './Card';
import questions from './questions';
import './App.css';

function App() {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [openedCards, setOpenedCards] = useState([]);

  const handleCardClick = () => {
    setOpenedCards([questions[currentQuestionIndex], ...openedCards]);
    setCurrentQuestionIndex((prevIndex) => (prevIndex + 1) % questions.length);
  };

  return (
    <div className='flex flex-col items-center min-h-screen bg-gray-100 p-8'>
      <h1 className='text-3xl font-bold mb-4 text-center'>Connection Cards</h1>
      <p className='mb-6 text-xl text-center'>Play a game of genuine connection.</p>
      <div className='flex-grow flex items-center'>
        <div className='opened-cards'>
          {openedCards.map((question, index) => (
            <Card key={index} question={question} />
          ))}
        </div>
        <div onClick={handleCardClick}>
          <Card question={questions[currentQuestionIndex]} />
        </div>
      </div>
    </div>
  );
}

export default App;
