import React from 'react';

const Card = ({ question }) => {
    if (!question) return <p>Loading...</p>;

    return (
        <div className='w-60 h-84 bg-white shadow-lg rounded-md flex items-center justify-center cursor-pointer'>
                <p className='text-center'>{question}</p>
        </div>
    );
}

export default Card;
