import React from 'react';

const Card = ({ question }) => {
    if (!question) return <p>Loading...</p>;

    return (
        <div className='w-100 h-100 bg-white shadow-lg rounded p-4 pointer'>
            <div className='h-full flex items-center justify-center pointer'>
                <p className='text-center pointer'>{question}</p>
            </div>
        </div>
    );
}

export default Card;
